import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PredictProjectStatus$Params } from 'src/app/servicesAbir/fn/project-controller/predict-project-status';
import { PredictionResponseDto } from 'src/app/servicesAbir/models/prediction-response-dto';
import { ProjectDto } from 'src/app/servicesAbir/models/project-dto';
import { ProjectControllerService } from 'src/app/servicesAbir/services';
import { StatusPredictionControllerService } from 'src/app/servicesAbir/services/status-prediction-controller.service';

@Component({
  selector: 'app-prediction-statut',
  templateUrl: './prediction-statut.component.html',
  styleUrls: ['./prediction-statut.component.css']
})
export class PredictionStatutComponent {
  predictionForm: FormGroup;
  predictionResult: PredictionResponseDto | null = null;
  isLoading = false;
  errorMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private apiService: StatusPredictionControllerService
  ) {
    this.predictionForm = this.fb.group({
      name: ['', Validators.required],
      duration: ['', [Validators.required, Validators.min(0)]]
    });
  }

  predictStatus(): void {
    if (this.predictionForm.invalid) {
      return;
    }

    this.isLoading = true;
    this.errorMessage = null;
    this.predictionResult = null;

    const projectData: ProjectDto = {
      name: this.predictionForm.value.name,
      duration: this.predictionForm.value.duration
    };

    this.apiService.predictStatus({ body: projectData }).subscribe({
      next: (response) => {
        this.predictionResult = response;
        this.isLoading = false;
      },
      error: (err) => {
        this.errorMessage = 'Erreur lors de la prédiction: ' + (err.error?.message || err.message);
        this.isLoading = false;
      }
    });
  }

  resetForm(): void {
    this.predictionForm.reset();
    this.predictionResult = null;
    this.errorMessage = null;
  }

}
<div class="prediction-container">
  <h2>Prédiction du statut du projet</h2>
  
  <form [formGroup]="predictionForm" (ngSubmit)="predictStatus()">
    <div class="form-group">
      <label for="name">Nom du projet:</label>
      <input id="name" type="text" formControlName="name" class="form-control">
      <div *ngIf="predictionForm.get('name')?.invalid && predictionForm.get('name')?.touched" 
           class="error-message">
        Le nom du projet est requis
      </div>
    </div>

    <div class="form-group">
      <label for="duration">Durée (en jours):</label>
      <input id="duration" type="number" formControlName="duration" class="form-control">
      <div *ngIf="predictionForm.get('duration')?.invalid && predictionForm.get('duration')?.touched" 
           class="error-message">
        Une durée valide est requise
      </div>
    </div>

    <div class="button-group">
      <button type="submit" class="btn btn-primary" [disabled]="predictionForm.invalid || isLoading">
        <span *ngIf="!isLoading">Prédire le statut</span>
        <span *ngIf="isLoading">En cours...</span>
      </button>
      <button type="button" class="btn btn-secondary" (click)="resetForm()">Réinitialiser</button>
    </div>
  </form>

  <div *ngIf="errorMessage" class="alert alert-danger">
    {{ errorMessage }}
  </div>

  <div *ngIf="predictionResult" class="result-container">
    <h3>Résultat de la prédiction</h3>
    <div class="result-card" [ngClass]="{
          'completed': predictionResult.prediction === 'COMPLETED',
          'delayed': predictionResult.prediction === 'DELAYED',
          'ongoing': predictionResult.prediction === 'ON_GOING'
        }">
      <p>Statut prédit: <strong>{{ predictionResult.prediction }}</strong></p>
    </div>
  </div>
</div>
