import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { SupplierService } from 'src/app/service-arij/supplier.service';

@Component({
  selector: 'app-supplier-list',
  templateUrl: './supplier-list.component.html',
  styleUrls: ['./supplier-list.component.css']
})
export class SupplierListComponent implements OnInit {
  suppliers: any[] = [];
  isLoading = true;
  errorMessage = '';
  searchText: string = '';
  

  constructor(private supplierService: SupplierService, private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      const status = params['status'];
      const newThisMonth = params['newThisMonth'] === 'true';
  
      if (newThisMonth) {
        this.loadSuppliers(undefined, true);
      } else if (status) {
        this.loadSuppliers(status);
      } else {
        this.loadSuppliers();
      }
    });
  }

  loadSuppliers(status?: string, newThisMonth: boolean = false): void {
    this.supplierService.getSuppliers().subscribe(
      (data) => {
        const today = new Date();
        const currentMonth = today.getMonth();
        const currentYear = today.getFullYear();
  
        this.suppliers = data.filter(supplier => {
          if (status) {
            return supplier.status === status;
          }
  
          if (newThisMonth) {
            const created = new Date(supplier.createdAt);
            return created.getMonth() === currentMonth && created.getFullYear() === currentYear;
          }
  
          return true;
        });
  
        this.isLoading = false;
      },
      (error) => {
        console.error('Error fetching suppliers:', error);
        this.errorMessage = 'Failed to load suppliers.';
        this.isLoading = false;
      }
    );
  }

  //////
  
  
  
  



  deleteSupplier(id: number): void {
    if (confirm('Are you sure you want to delete this supplier?')) {
      this.supplierService.deleteSupplier(id).subscribe(() => {
        this.suppliers = this.suppliers.filter(supplier => supplier.idSupplier !== id);
        alert('Supplier deleted successfully!');
      }, error => {
        console.error('Error deleting supplier:', error);
        alert('Failed to delete supplier.');
      });
    }
  }

  

  filteredSuppliers() {
    return this.suppliers.filter(supplier =>
      (supplier.name?.toLowerCase() ?? '').includes(this.searchText.toLowerCase()) ||
      (supplier.address?.toLowerCase() ?? '').includes(this.searchText.toLowerCase()) ||
      (supplier.materialResource?.firstName?.toLowerCase() ?? '').includes(this.searchText.toLowerCase())
    );
  }
  
  

  navigateToAddSupplier(): void {
    this.router.navigate(['/add-supplier']);  // ‚úÖ Navigate to add supplier page
  }

  viewSupplier(id: number): void {
    
    this.supplierService.incrementClickCount(id).subscribe(() => {
      this.router.navigate(['/supplier', id]);
 // Navigate to details page
    });
  }




  navigateToDashboard(): void {
    this.router.navigate(['/dashboard']);
  }
  
  
}
<div class="supplier-list-container">
  <h1 class="page-title">üì¶ Supplier List</h1>

  <!-- Search Bar -->
  <div class="search-container">
    <input type="text" [(ngModel)]="searchText" placeholder="üîç Search suppliers..." class="search-box" />
  </div>

  <!-- Loading and Error Messages -->
  <p *ngIf="isLoading" class="loading">Loading suppliers...</p>
  <p *ngIf="errorMessage" class="error-message">{{ errorMessage }}</p>

    
<!-- Go to Dashboard Button -->
<button class="btn-dashboard" (click)="navigateToDashboard()">Go to Dashboard</button>



  <!-- Supplier Grid -->
  <div class="supplier-grid">
    <div class="supplier-card" *ngFor="let supplier of filteredSuppliers()">
      <h3>{{ supplier.name }}</h3>
      <p><strong>üìç Address:</strong> {{ supplier.address }}</p>
      <p><strong>üîß Material:</strong> {{ supplier.materialResource?.firstName || 'Not Assigned' }}</p>

      <div class="status-badge" [ngClass]="{
          'active': supplier.status === 'ACTIVE',
          'inactive': supplier.status === 'INACTIVE'
        }">
        {{ supplier.status }}
      </div>

      <div class="button-group">
        <button class="btn-view" (click)="viewSupplier(supplier.idSupplier)">
          <i class="fas fa-eye"></i> View
        </button>
        <button class="btn-delete" (click)="deleteSupplier(supplier.idSupplier)">
          <i class="fas fa-trash"></i> Delete
        </button>
      </div>
    </div>
  </div>
</div>

<!-- Floating Add Button -->
<button class="btn-add" (click)="navigateToAddSupplier()">Add Supplier ‚ûï</button>
