export class TasksComponent {
  constructor(taskService, ganttChartService, router, modalService) {
    this.taskService = taskService;
    this.ganttChartService = ganttChartService;
    this.router = router;
    this.modalService = modalService;
    this.tasks = [];
    this.filteredTasks = [];
    this.selectedTaskIds = new Set();
    this.searchQuery = '';
    this.qrCodeData = '';
    this.currentPage = 1;
    this.itemsPerPage = 4;
    this.showPredictionForm = false;
    this.predictedDuration = null;
    this.errorMessage = null;
    this.taskDescription = '';
    this.ganttInitialized = false;
    this.trendAnalysisResult = '';
    this.isLoadingAnalysis = false;
  }
  ngAfterViewInit() {
    this.initializeGantt();
  }
  ngOnInit() {
    this.loadTasks();
  }
  initializeGantt() {
    if (typeof gantt === 'undefined') {
      console.error('Gantt library not loaded!');
      setTimeout(() => this.initializeGantt(), 100);
      return;
    }
    try {
      gantt.config.date_format = "%d-%m-%Y";
      gantt.config['scale_unit'] = "day";
      gantt.config['subscales'] = [{
        unit: "day",
        step: 1,
        date: "%j, %D"
      }];
      gantt.init("gantt-container");
      this.ganttInitialized = true;
    } catch (error) {
      console.error('Gantt initialization failed:', error);
    }
  }
  predictTaskDuration(name, description) {
    const params = {
      body: {
        name: name,
        description: description
      }
    };
    this.taskService.predictDuration(params).subscribe({
      next: response => {
        this.predictedDuration = response['The expected duration is '];
        this.errorMessage = null;
      },
      error: err => {
        this.errorMessage = 'Failed to predict duration: ' + err.message;
        this.predictedDuration = null;
      }
    });
  }
  startVoiceSearch() {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.onstart = () => {
        console.log('Voice recognition started. Speak now...');
      };
      recognition.onresult = event => {
        const transcript = event.results[0][0].transcript;
        this.searchQuery = transcript;
        this.filterTasks();
        recognition.stop();
      };
      recognition.onerror = event => {
        console.error('Voice recognition error:', event.error);
        recognition.stop();
      };
      recognition.onend = () => {
        console.log('Voice recognition ended.');
      };
      recognition.start();
    } else {
      alert('Your browser does not support voice recognition. Please use Chrome or another supported browser.');
    }
  }
  getStatusTrendAnalysis(modalContent) {
    this.isLoadingAnalysis = true;
    this.trendAnalysisResult = '';
    this.taskService.getStatusTrendAnalysis().subscribe({
      next: response => {
        this.trendAnalysisResult = response;
        this.modalService.open(modalContent, {
          size: 'lg'
        });
        this.isLoadingAnalysis = false;
      },
      error: error => {
        this.trendAnalysisResult = 'Error loading analysis: ' + error.message;
        this.modalService.open(modalContent, {
          size: 'lg'
        });
        this.isLoadingAnalysis = false;
      }
    });
  }
  showQRCode(task, qrModal) {
    const formatDate = dateString => {
      const date = new Date(dateString);
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    };
    this.qrCodeData = `
      Name: ${task.name}
      Description: ${task.description}
      Start Date: ${formatDate(task.startDate)}
      Planned End Date: ${formatDate(task.planned_end_date)}
      Status: ${task.status}
    `;
    this.modalService.open(qrModal, {
      size: 'lg'
    });
  }
  exportToExcel() {
    const data = [];
    const headers = ['Task Name', 'Description', 'Start Date', 'Planned End Date', 'Status'];
    data.push(headers);
