import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { RegistrationRequest } from 'src/app/services/models';
import { AuthenticationService } from 'src/app/services/services';
import { TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerRequest: RegistrationRequest = { email: '', firstName: '', lastName: '', password: '', dateOfBirth: new Date() };
  confirmPassword: string = '';
  errorMsg: Array<string> = [];
  showPassword = false;

  constructor(
    private router: Router,
    private authService: AuthenticationService,
    private translate: TranslateService
  ) {}

  login() {
    this.router.navigate(['login']);
  }

  register() {
    this.errorMsg = [];

    if (this.registerRequest.password !== this.confirmPassword) {
      this.errorMsg.push('Passwords do not match');
      return;
    }

    this.authService.register({
      body: this.registerRequest
    }).subscribe({
      next: () => this.router.navigate(['activate-account']),
      error: (err) => {
        this.errorMsg = err.error.validationErrors;
      }
    });
  }

  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

  switchLanguage(event: Event) {
    const selectedLang = (event.target as HTMLSelectElement).value;
    if (selectedLang) {
      this.translate.use(selectedLang);
    }
  }
}
<div class="full-screen-background">
  <div class="container">
    <div class="row justify-content-center">
      <div class="col-12 col-sm-8 col-md-6 col-lg-4">
        <div class="card login-card p-4">
          <select (change)="switchLanguage($event)" class="language-selector form-select mb-3">
            <option value="en">English</option>
            <option value="fr">Français</option>
            <option value="ar">العربية</option>
          </select>
          
          <h3 class="login-text text-center">{{ 'CREATE_ACCOUNT' | translate }}</h3>
          <hr>

          <div class="alert alert-danger" role="alert" *ngIf="errorMsg.length">
            <p *ngFor="let msg of errorMsg">{{ msg }}</p>
          </div>

          <div class="mb-3">
            <label for="firstname" class="form-label">{{ 'FIRSTNAME' | translate }}</label>
            <input [(ngModel)]="registerRequest.firstName" type="text" class="form-control" id="firstname" placeholder="{{ 'ENTER_FIRSTNAME' | translate }}">
          </div>

          <div class="mb-3">
            <label for="lastname" class="form-label">{{ 'LASTNAME' | translate }}</label>
            <input [(ngModel)]="registerRequest.lastName" type="text" class="form-control" id="lastname" placeholder="{{ 'ENTER_LASTNAME' | translate }}">
          </div>

          <div class="mb-3">
            <label for="dateOfBirth" class="form-label">{{ 'BIRTHDATE' | translate }}</label>
            <input [(ngModel)]="registerRequest.dateOfBirth" type="date" class="form-control" id="dateOfBirth" placeholder="{{ 'ENTER_DATEOFBIRTH' | translate }}">
          </div>

          <div class="mb-3">
            <label for="login" class="form-label">{{ 'EMAIL' | translate }}</label>
            <input [(ngModel)]="registerRequest.email" type="email" class="form-control" id="login" placeholder="{{'name@example.com' | translate }}">
          </div>

          <div class="mb-3 position-relative">
            <label for="password" class="form-label">{{ 'PASSWORD' | translate }}</label>
            <div class="position-relative">
              <input 
                [(ngModel)]="registerRequest.password" 
                [type]="showPassword ? 'text' : 'password'"
                class="form-control pr-5" 
                id="password" 
                placeholder="{{'ENTER_PASSWORD' | translate }}"
              >
              <i 
                class="fas" 
                [ngClass]="showPassword ? 'fa-eye-slash' : 'fa-eye'"
                (click)="togglePasswordVisibility()" 
                style="position: absolute; right: 15px; top: 50%; transform: translateY(-50%); cursor: pointer; color: gray;">
              </i>
            </div>
          </div>
          <div class="mb-3 position-relative">
            <label for="confirmPassword" class="form-label">{{ 'CONFIRM_PASSWORD' | translate }}</label>
            <div class="position-relative">
              <input 
                [(ngModel)]="confirmPassword" 
                [type]="showPassword ? 'text' : 'password'" 
                class="form-control pr-5" 
                id="confirmPassword" 
                placeholder="{{ 'RETYPE_PASSWORD' | translate }}"
              >
              <i 
                class="fas" 
                [ngClass]="showPassword ? 'fa-eye-slash' : 'fa-eye'" 
                (click)="togglePasswordVisibility()" 
                style="position: absolute; right: 15px; top: 50%; transform: translateY(-50%); cursor: pointer; color: gray;">
              </i>
            </div>
          </div>
          

          <div class="d-flex justify-content-between mb-3">
